package week1.day1;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
                int n = 5;
                 int op = 1;
                 for(int i=1;i<=n;i++) {
                	 op = op*i;
                	 System.out.println(op);
                 }
	}
}

